  `author` varchar(15) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '作者',
  `thumb` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '封面图',
  `time` timestamp(0) NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
